        if (this.spec != null) {
            this.formGroup.setValue(this.spec.data)
            this.treeNodes = []
        }
    }

    formGroup: FormGroup = new FormGroup({
        environment : new FormControl(null, [Validators.required]),
        name : new FormControl(null, [Validators.required]),
    })

