            this.treeNodes = new StructureDto2TreeNodeConverter().run(structureDto)
        })
    }

    onNameChangedManually() {
        this.spec.useNameGenerator = false
    }

    cancel() {
        this.spec.cancel()
    }

    confirm() {
        this.test()
        this.spec.confirm(this.formGroup.value)
    }

}

