@Injectable()
export class ConsoleService {

    constructor(private consoleControllerService: ConsoleControllerService) {
    }

    loadDescriptor(systemComponentKey: string) : Promise<ConsoleDescriptorDto>{
        return this.consoleControllerService.getDescriptorUsingGet({systemComponentKey})
            .toPromise()
    }

    runRequest(environmentKey: string, systemComponentKey: string, attributes, navItem: NavItem): Promise<ConsoleResponseItemDto> {
        return this.consoleControllerService.runRequestUsingPost({
            body: {
                environmentKey,
                systemComponentKey,
                attributes,
                navItem
            }
        }).toPromise()
    }

    loadSnippets(systemComponentKey:string): Promise<SnippetDto[]> {
        return this.consoleControllerService.getSnippetsUsingGet({systemComponentKey}).toPromise()
    }

    addSnippet(title: string, systemComponentKey: string, attributes: {}): Promise<SnippetDto[]> {
        return this.consoleControllerService.createSnippetUsingPost({
            systemComponentKey,
            body: {
                title,
                attributes
            }
        }).toPromise().then(()=> this.loadSnippets(systemComponentKey))

    }

    loadNavItems(systemComponentKey: string): Promise<NavItem[]>  {
        return this.consoleControllerService.getNavigationUsingPost({body: {
            systemComponentKey,
        }}).toPromise()
    }
}

import {Injectable, OnDestroy} from '@angular/core';
import {SnapshotControllerService} from "@fabric/services";
import {BehaviorSubject, Subscription} from "rxjs";
import {distinctUntilChanged, map} from "rxjs/operators";
import {ExecutionId, SnapshotId, SnapshotSummaryDto} from "@fabric/models";

interface DashboardStoreState {
    summaries: SnapshotSummaryDto[]
}

@Injectable({
    providedIn: 'root'
})
export class DashboardService implements OnDestroy {

    constructor(private snapshotControllerService: SnapshotControllerService) {
        this.init()
    }

    private store = new BehaviorSubject<DashboardStoreState>({ summaries: []})

    summaries$ = this.store.pipe(
        map(state => state.summaries),
        distinctUntilChanged(),
    )

    private subscriptions: Subscription[] = []

    init() {
        this.loadSummaries();
    }

    ngOnDestroy(): void {
        this.subscriptions.forEach(s => s.unsubscribe())
    }

    private loadSummaries() {
        this.snapshotControllerService.listUsingGet({}).subscribe(summaries => {
            this.store.next({summaries: summaries})
        })
    }

    createSnapshot(environment: string, name: string) {
        this.snapshotControllerService.createUsingPost({
            body: {
                environmentKey: environment,
                name
            }
        }).subscribe(
        (dto) => this.loadSummaries()
        )
    }

    delete(snapshotId: SnapshotId) {
        this.snapshotControllerService.deleteByIdUsingDelete({id: snapshotId.id}).subscribe(()=> {
            this.loadSummaries()
        })

    }

    upload(snapshotId: SnapshotId, envKey: string): Promise<ExecutionId> {
        return this.snapshotControllerService.uploadUsingPost({
            environmentKey: envKey,
            id: snapshotId.id
        }).toPromise()
    }
}

