    this.openMetaModelDialog({
      systemComponent: this.systemComponent,
      systemDescription,
      data: {
        environmentKey: null,
      },
      confirm: (data: MetaModelDialogData) => {
        this.closeMetaModelDialog()
        this.service.fetchMetaModel(this.systemComponent, data.environmentKey).then((structureDto =>  this.structureDTo = structureDto))
      },
      cancel: () => this.closeMetaModelDialog()
    })
  }

}

import {Component, ElementRef, Input, OnDestroy, OnInit, ViewEncapsulation} from '@angular/core';

import {ModalService} from './modal-service';

// based on https://github.com/cornflourblue/angular-10-custom-modal
// licence MIT

@Component({
  selector: 'jw-modal',
  styleUrls: ['./modal.component.css'],
  templateUrl: 'modal.component.html',
  encapsulation: ViewEncapsulation.None
})
export class ModalComponent implements OnInit, OnDestroy {

  @Input() id: string;
  private element: any;

  constructor(private modalService: ModalService, private el: ElementRef) {
    this.element = el.nativeElement;
  }

  ngOnInit(): void {
    if (!this.id) {
      console.error('modal must have an id');
      return;
    }

    document.body.appendChild(this.element);

    this.element.addEventListener('click', el => {
      if (el.target.className === 'jw-modal') {
        this.close();
      }
    });

    this.modalService.add(this);
  }

  ngOnDestroy(): void {
    this.modalService.remove(this.id);
    this.element.remove();
  }

  // open modal
  open(): void {
    this.element.style.display = 'block';
    document.body.classList.add('jw-modal-open');
  }

  // close modal
  close(): void {
    this.element.style.display = 'none';
    document.body.classList.remove('jw-modal-open');
  }
}

