src/app/common/validators.ts.defaultValue:
function (fieldName: string, values: string[]): ValidatorFn {
    return (control: AbstractControl): ValidationErrors | null => {
        let controlValue: string = control.value;
        const invalid = controlValue && !!values.find(val => val === controlValue)
        return invalid ? {unique: { value: `The ${fieldName} '${controlValue}' is already in use.`}} : null;
    }
}
